import { warning } from "@remix-run/router";
import axios from "axios";
import Swal from "sweetalert2";

export default function PedidosBorrar(id)
{
    function refrescar()
    {
        window.location.href="./pedidoslistar";
    }

    const sw = Swal.mixin({
        customClass:{
            confirmButton: 'btn btn-success',
            cancelButton: 'btn btn-danger'
        },
        buttonsStyling:false
    })
    sw.fire({
        title: '¿Desea eliminar el registro?',
        text: 'No es posible deshacer el borrado de registros.',
        icon: 'warning',
        showCancelButton: true,
        confirmButtonText: 'Ok',
        cancelButtonText: 'Cancelar',
        reverseButtons:false
    }).then((result)=>{
        if(result.isConfirmed)
        {
            axios.delete('/api/pedidos/borrar/${id}').then(()=>this.setState({status: 'Borrado Exitoso'}));
            refrescar();

            sw.fire(
                'Operación exitosa',
                'El registro fue eliminado correctamente',
                'success'
            )
        }
        else if(result.dismiss === Swal.DismissReason.cancel)
        {
            sw.fire(
                'Error',
                'El registro no pudo ser borrado',
                "error"
            )
        }
    })
}
